import React from "react";
import { Post } from "../../components/Post";
import styles from "./AllPosts.module";
import dateConverter from "../../utils/dateConverter";
import { useSelector } from "react-redux";
export default function AllPosts() {
  const { posts } = useSelector((state) => state.posts);
  const isLoading = posts.status === "loading";
  return (
    <div className={styles.container}>
      {(isLoading ? [...Array(5)] : posts).map((item) =>
        isLoading ? (
          <Post isLoading={isLoading} />
        ) : (
          <Post
            id={item._id}
            title={item.title}
            imageUrl={item.image ? `http://localhost:4420${item.image}` : ""}
            user={item.user}
            createdAt={dateConverter(item.createdAt)}
            viewsCount={item.views}
            commentsCount={3}
            tags={item.tags}
            //comparing post-userId and userId-from-store
            isEditable={Boolean(item.user._id === userData?._id)}
          />
        )
      )}
    </div>
  );
}
