import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from '../../axios.js'

export const fetchSignIn = createAsyncThunk('auth/fetch', async (params) => {
    const { data } = axios.post('/auth/login', params)
    return data
})
export const fetchSignUp = createAsyncThunk('auth/fetch', async (params) => {
    const { data } = axios.post('/auth/reg', params)
    return data
})

const initialState = {
    data: null,
    status: 'loading',
    isAuth: false
}

const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {

    },
    extraReducers: (builder) => {
        builder
            .addMatcher((action) => [
                fetchSignIn.pending, fetchSignUp.pending
            ].includes(action.type), (state) => {
                state.data = null
                state.status = 'loading'
            })
            .addMatcher((action) => [
                fetchSignIn.fulfilled, fetchSignUp.fulfilled
            ].includes(action.type), (state, action) => {
                state.data = action.payload
                state.status = 'success'
                state.isAuth = true
            })
            .addMatcher((action) => [
                fetchSignIn.pending, fetchSignUp.pending
            ].includes(action.type), (state) => {
                state.data = null
                state.status = 'error'
            })
    }
})

const authReducer = authSlice.reducer
export default authReducer
