import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from '../../axios'

// make request to back for getting a posts 
export const fetchGetPosts = createAsyncThunk('post/fetch', async () => {
    const { data } = await axios.get('/posts')
    return data
})

const initialState = {
    posts: {
        items: [],
        status: 'loading'
    },
    tags: {
        items: [],
        status: 'loading'
    }

}

const postSlice = createSlice({
    name: 'post',
    initialState,
    reducers: {},
    //describe a state asyncthunk
    extraReducers: {
        [fetchGetPosts.pending]: (state) => {
            state.posts.items = []
            state.posts.status = 'loading'
        },
        [fetchGetPosts.fulfilled]: (state, action) => {
            state.posts.items = action.payload
            state.posts.status = 'success'
        },
        [fetchGetPosts.rejected]: (state) => {
            state.posts.status = 'error'
        }
    }
})

const postReducer = postSlice.reducer
export default postReducer
