import React, { useEffect } from "react";
import { Post } from "../../components/Post";
import styles from "./AllPosts.module.scss";
import dateConverter from "../../utils/dateConverter";
import { useSelector, useDispatch } from "react-redux";
import { fetchPosts } from "../../redux/slice/postSlice";
import { useParams } from "react-router-dom";
export default function AllPosts() {
  const { posts } = useSelector((state) => state.post);
  const dispatch = useDispatch();
  const tagName = useParams();
  const isLoading = posts.status === "loading";
  useEffect(() => {
    try {
      dispatch(fetchPosts());
    } catch (error) {
      console.warn(error);
    }
  }, []);
  const fulteredPosts = Array.isArray(posts?.items) // Check if posts.items is an array
    ? [...posts.items].sort(function (a, b) {
        // If it is, create a copy of the array and sort it
        if (a.views > b.views) return -1; // Compare the views count of two posts
        return 0; // If the views count is the same, maintain the order
      })
    : []; // If posts.items is not an array, set popular as an empty array
  console.log(fulteredPosts);
  return (
    <div className={styles.container}>
      {(isLoading ? [...Array(5)] : posts.items).map((item) =>
        isLoading ? (
          <Post isLoading={isLoading} />
        ) : (
          <Post
            id={item._id}
            title={item.title}
            imageUrl={item.image ? `http://localhost:4420${item.image}` : ""}
            user={item.user}
            createdAt={dateConverter(item.createdAt)}
            viewsCount={item.views}
            commentsCount={3}
            tags={item.tags}
          />
        )
      )}
    </div>
  );
}
