import React from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Grid from "@mui/material/Grid";
import { Post } from "../components/Post";
import { TagsBlock } from "../components/TagsBlock";
import { CommentsBlock } from "../components/CommentsBlock";
import { useDispatch, useSelector } from "react-redux";
import { fetchPosts, fetchTags } from "../redux/slice/postSlice";
import axios from "../axios";
export const Home = () => {
  const dispatch = useDispatch();
  //pull out posts and tags from postReducer
  const { posts, tags } = useSelector((state) => state.post);
  //pulling out the status for checking if posts are loaded
  const isPostsLoading = posts.status === "loading";
  const isTagsLoading = tags.status === "loading";
  console.log(posts);

  React.useEffect(() => {
    try {
      const { data } = axios.get("/posts");
      console.log(data);
      dispatch(fetchPosts);
      dispatch(fetchTags);
    } catch (error) {
      console.log("failure to get posts", error);
    }
  }, []);

  return (
    <>
      <Tabs
        style={{ marginBottom: 15 }}
        value={0}
        aria-label="basic tabs example"
      >
        <Tab label="New" />
        <Tab label="Popular" />
      </Tabs>
      <Grid container spacing={4}>
        <Grid xs={8} item>
          {(isPostsLoading ? [...Array(5)] : posts.items).map((item) =>
            isPostsLoading ? (
              <Post isLoading={isPostsLoading} />
            ) : (
              <Post
                id={item._id}
                title={item.title}
                imageUrl={item.image}
                user={item.user}
                createdAt={item.createdAt}
                viewsCount={item.views}
                commentsCount={3}
                tags={item.tags}
                isEditable
              />
            )
          )}
        </Grid>
        <Grid xs={4} item>
          <TagsBlock items={tags.items} isLoading={isTagsLoading} />
          <CommentsBlock
            items={[
              {
                user: {
                  fullName: "Вася Пупкин",
                  avatarUrl: "https://mui.com/static/images/avatar/1.jpg",
                },
                text: "Это тестовый комментарий",
              },
              {
                user: {
                  fullName: "Иван Иванов",
                  avatarUrl: "https://mui.com/static/images/avatar/2.jpg",
                },
                text: "When displaying three lines or more, the avatar is not aligned at the top. You should set the prop to align the avatar at the top",
              },
            ]}
            isLoading={false}
          />
        </Grid>
      </Grid>
    </>
  );
};
