import React from "react";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Button from "@mui/material/Button";
import Avatar from "@mui/material/Avatar";

import styles from "./Login.module.scss";
import { useForm } from "react-hook-form";
export const Registration = () => {
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      email: "sex@gamil.com",
      password: "2231231123",
    },
    mode: "onChange",
  });
  const Submit = async (data) => {
    //declarate user data to varible
    const user = await dispatch(fetchSignIn(data));
    //if user data does`t exist return alert
    if (!user.payload) {
      alert("failure to login");
    }
    // check if record 'tokek' exist save it to localStorage
    if ("token" in user.payload) {
      localStorage.setItem("auth-token", user.payload.token);
    }
    // dispatch(fetchSignIn(data)).then((res) => {
    //   if (!res.payload) {
    //     alert("failure to login");
    //   }
    //   if ("token" in res.payload) {
    //     localStorage.setItem("auth-token", res.payload.token);
    //   }
    // });
  };
  return (
    <Paper classes={{ root: styles.root }}>
      <Typography classes={{ root: styles.title }} variant="h5">
        Create account
      </Typography>
      <div className={styles.avatar}>
        <Avatar sx={{ width: 100, height: 100 }} />
      </div>
      <TextField className={styles.field} label="Полное имя" fullWidth />
      <TextField className={styles.field} label="E-Mail" fullWidth />
      <TextField className={styles.field} label="Пароль" fullWidth />
      <Button size="large" variant="contained" fullWidth>
        Registration
      </Button>
    </Paper>
  );
};
